#!/bin/bash

# MongoDB Security Hardening Script
# This script secures MongoDB to prevent ransomware attacks

set -e

echo "🛡️ MongoDB Security Hardening"
echo ""

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

CONTAINER_NAME="smokava-mongodb"
COMPOSE_FILE="/opt/smokava/docker-compose.yml"

echo "📋 Step 1: Checking current MongoDB configuration..."
if docker ps | grep -q "$CONTAINER_NAME"; then
    echo -e "${GREEN}✅ MongoDB container is running${NC}"
else
    echo -e "${RED}❌ MongoDB container is not running${NC}"
    exit 1
fi

echo ""
echo "🔒 Step 2: Checking if MongoDB port is exposed..."
EXPOSED_PORT=$(docker port "$CONTAINER_NAME" 2>/dev/null | grep 27017 || echo "")
if [ -n "$EXPOSED_PORT" ]; then
    echo -e "${YELLOW}⚠️  WARNING: MongoDB port 27017 is exposed to the internet!${NC}"
    echo "   This is a security risk and likely how the ransomware attack occurred."
    echo ""
    echo "   To fix:"
    echo "   1. Edit $COMPOSE_FILE"
    echo "   2. In the mongodb service, remove or comment out:"
    echo "      ports:"
    echo "        - \"27017:27017\""
    echo "   3. Restart: docker compose restart mongodb"
    echo ""
    read -p "Do you want to remove the port mapping now? (yes/no): " remove_port
    if [ "$remove_port" = "yes" ]; then
        echo "📝 Removing port mapping from docker-compose.yml..."
        # This would require sed/awk to edit the file
        echo -e "${YELLOW}⚠️  Please manually edit $COMPOSE_FILE to remove the port mapping${NC}"
    fi
else
    echo -e "${GREEN}✅ MongoDB port is not exposed externally${NC}"
fi

echo ""
echo "🔐 Step 3: Checking MongoDB authentication..."
# Check if authentication is enabled
AUTH_CHECK=$(docker exec "$CONTAINER_NAME" mongosh admin --quiet --eval 'db.getUsers().length' 2>/dev/null || echo "0")
if [ "$AUTH_CHECK" = "0" ] || [ -z "$AUTH_CHECK" ]; then
    echo -e "${YELLOW}⚠️  WARNING: MongoDB authentication is not enabled!${NC}"
    echo ""
    echo "   To enable authentication:"
    echo "   1. Create admin user:"
    echo "      docker exec $CONTAINER_NAME mongosh admin --eval 'db.createUser({user: \"admin\", pwd: \"STRONG_PASSWORD\", roles: [{role: \"root\", db: \"admin\"}]})'"
    echo "   2. Update MONGODB_URI in docker-compose.yml:"
    echo "      mongodb://admin:STRONG_PASSWORD@mongodb:27017/smokava?authSource=admin"
    echo "   3. Restart MongoDB with --auth flag"
else
    echo -e "${GREEN}✅ MongoDB has users configured${NC}"
fi

echo ""
echo "🔥 Step 4: Setting up firewall rules..."
if command -v ufw &> /dev/null; then
    echo "Checking UFW rules for MongoDB..."
    if ufw status | grep -q "27017"; then
        echo -e "${GREEN}✅ Firewall rules exist for MongoDB${NC}"
    else
        echo -e "${YELLOW}⚠️  No firewall rules found for MongoDB${NC}"
        echo "   To block MongoDB port: sudo ufw deny 27017"
    fi
elif command -v iptables &> /dev/null; then
    echo "Checking iptables rules for MongoDB..."
    if iptables -L INPUT -n | grep -q "27017"; then
        echo -e "${GREEN}✅ Firewall rules exist for MongoDB${NC}"
    else
        echo -e "${YELLOW}⚠️  No firewall rules found for MongoDB${NC}"
        echo "   To block MongoDB port: sudo iptables -A INPUT -p tcp --dport 27017 -j DROP"
    fi
else
    echo -e "${YELLOW}⚠️  No firewall tool found (ufw or iptables)${NC}"
fi

echo ""
echo "💾 Step 5: Setting up automated backups..."
BACKUP_SCRIPT="/opt/smokava/scripts/backup-mongodb.sh"
if [ -f "$BACKUP_SCRIPT" ]; then
    echo -e "${GREEN}✅ Backup script exists${NC}"

    # Check if cron job exists
    if crontab -l 2>/dev/null | grep -q "backup-mongodb"; then
        echo -e "${GREEN}✅ Automated backup is configured${NC}"
    else
        echo -e "${YELLOW}⚠️  Automated backup is not configured${NC}"
        echo "   To set up daily backups at 2 AM:"
        echo "   crontab -e"
        echo "   Add: 0 2 * * * $BACKUP_SCRIPT >> /var/log/mongodb-backup.log 2>&1"
    fi
else
    echo -e "${YELLOW}⚠️  Backup script not found at $BACKUP_SCRIPT${NC}"
fi

echo ""
